positions = [
  {name: 'start', fen: 'start'},
  {name: 'intro', fen: 'r1bqkbnr/pppp1ppp/2n5/4p3/3PP3/5N2/PPP2PPP/RNBQKB1R'}
]







board_setup = function(data, title, pos) {

  var board = Chessboard('myBoard-' + title)

  const position = positions.find(d => d.name === pos).fen
  board.position(position, false)

  const moves = data.find(d => d.title === title).moves
  var pos = -1 

  $('#next-' + title).on('click', function () {
    if (pos <= moves.length) {
      pos = pos + 1
      board.move(moves[pos])
    }
  })

  $('#prev-' + title).on('click', function () {
    if (pos >= 0) {
      var move_rev = moves[pos]
      move_rev = move_rev.substring(3, 5) + '-' + move_rev.substring(0, 2)
      board.move(move_rev)

      pos = pos - 1
    }
  })

  $('#reset-' + title).on('click', function() {
    pos = -1
    board.position(position)
  })

}







board_setup(scotch, "intro", "start")












